import React, {useState} from 'react';
import CancleIcon from '../Assets/Cancle-icon/cancel.svg'
import './Header.css';
import Logo from '../Assets/Images/LOGOs.svg';
import { motion } from "framer-motion"

function Header() {

    const [triCircleHover, setTriCircleHover] = useState('sb-dot');
    const [sideBarActive, setSidebarActive] = useState(1200)

    const tryCircleOver = () => {
        setTriCircleHover('sb-dot-active');
        console.log(triCircleHover)
    }
    
    const cancelSideBar = () => {
        setSidebarActive(1200)
    }

    const showSideBar = () => {
        setSidebarActive(0) 
    }

    const tryCircleLeave = () => {
        setTriCircleHover('sb-dot');
    }

    return (
        <div className='header'>
            <div className='band-cont'>
                <img className="brand-logo" src={Logo} alt=""/>
                <h2 className='brand-title'>TORTOISE DIGITAL</h2>
            </div>

            <div onClick={showSideBar} onMouseLeave={tryCircleLeave} onMouseEnter={tryCircleOver} className="sb-dot-cont">
                <div className={`${triCircleHover} sb-dot-1`}>
                </div>
                 <div className={`${triCircleHover} sb-dot-2`}>
                </div>
                <div className={`${triCircleHover} sb-dot-3`}>
                </div>
            </div>

            <motion.div
            //  className={`${sideBarActive}`}
             className="sidebar-active"
             animate={{
                x: sideBarActive,
                y: 0,
                animationDuration: 4,
                scale: 1,
                rotate: 0,
              }}
            >
                <div className="sidebar-container">
                    <img onClick={cancelSideBar} className="cancel-icon" src={CancleIcon} alt=""/>
                    <motion.div className="sidebar-link-cont"
                         animate={{
                            x: 0,
                            y: 0,
                            animationDuration: 4,
                            scale: 1,
                            rotate: 0,
                          }}
                    >
                            <a className="bar-link" href="#"><p className="sidebar-link">Home</p></a>
                            <a className="bar-link" href="#"><p className="sidebar-link">Agency</p></a>
                            <a className="bar-link" href="#"><p className="sidebar-link">Contact</p></a>
                    </motion.div>
                </div>
            </motion.div>
        </div>
    )
}

export default Header;
